mapper.select:
SELECT count( 1 ) as data , DATE_FORMAT( t.visitor_time, '%m' ) as date FROM ( SELECT a.* FROM ( select * from visitor
visitor_pageName not LIKE '%手机%'
) AS a
visitor_pageName LIKE '%${website}%'
) AS t WHERE DATE_FORMAT( t.visitor_time, '%y' ) = #{year} GROUP BY DATE_FORMAT( t.visitor_time, '%m' )

select
  count(1) as data ,
  DATE_FORMAT(t.visitor_time,'%e') as date
from (
	SELECT a.* FROM (
		select * from visitor


					visitor_pageName not LIKE '%手机%'


		) AS a


				visitor_pageName LIKE '%${website}%'


) as t
 where DATE_FORMAT(t.visitor_time,'%m') = #{month} and DATE_FORMAT( t.visitor_time, '%y' ) = #{year}
 GROUP BY  DATE_FORMAT(t.visitor_time,'%e')

select COUNT(1) from
	(
	SELECT a.* FROM (
		select * from visitor


					visitor_pageName not LIKE '%手机%'


		 ) AS a


		 		visitor_pageName LIKE '%${website}%'


	) as t
 where DATE_FORMAT(t.visitor_time,'%m')=#{month} and DATE_FORMAT( t.visitor_time, '%y' ) = #{year}

SELECT count( 1 ) FROM ( SELECT a.* FROM ( select * from visitor
visitor_pageName not LIKE '%手机%'
) AS a
visitor_pageName LIKE '%${website}%'
) as t WHERE DATE_FORMAT(t.visitor_time,'%m')=#{month} and DATE_FORMAT( t.visitor_time, '%y' ) = #{year} and DATE_FORMAT(visitor_time,'%e')=#{day}
